import { createSlice } from '@reduxjs/toolkit'

const uiCart = createSlice({
    name: 'ui',
    initialState: { cartshow: true },
    reducers: {
        toggle(state) {
            state.cartshow = !state.cartshow
        }
    }
})
export const cartAction = uiSlice.actions

export default uiSlice
